package com.example.restservice;import java.util.HashMap;import java.util.List;import java.util.Map;import java.util.concurrent.atomic.AtomicLong;import java.util.stream.Collectors;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.web.bind.annotation.*;@RestControllerpublic class PersonController {    private static final Logger log = LoggerFactory.getLogger(PersonController.class);    private static final String template = "Hello, %s!";    private final AtomicLong counter = new AtomicLong();    private static final Map<Long, Person> persons = new HashMap<>();    @GetMapping("/person/{id}")    public Person getPersonById(@PathVariable("id") Long id) {        return persons.get(id);    }    @GetMapping("/person")    public List<Person> getAllPersons() {        return persons.values().stream().collect(Collectors.toList());    }    @PostMapping("/person")    public String createPerson(@RequestBody Person person) {        log.debug("person:{}", person);        person.setId(Long.valueOf(counter.incrementAndGet()));        persons.put(person.getId(), person);        return "create person successfully";    }    @PutMapping("/person")    public String updatePerson(@RequestBody Person person) {        persons.put(person.getId(), person);        return "update person successfully";    }    @DeleteMapping("/person/{id}")    public String deletePerson(@PathVariable("id") Long id) {        persons.remove(id);        return "delete person successfully";    }}